INSERT INTO courses (id, title, description, price, visible) VALUES (2, 'Картопля фрі', 'Картопля фрі — шматочки картоплі, обсмажені у великій кількості розпеченої рослинної олії. Виготовляється, як правило, у спеціальних приладах — фритюрницях. Картопля фрі — популярна страва, що подається у закладах фастфуда.', 5000, 1);
INSERT INTO courses (id, title, description, price, visible) VALUES (3, 'Піца вегетеріанська', 'Піца вегетеріанська — італійська національна страва, а саме, корж зазвичай круглої форми, який покривається томатною пастою та сиром і запікається. У вегетеріанську піцу можуть додавати різні інгредієнти, такі як овочі, гриби, зелень та інші.', 15000, 1);
INSERT INTO courses (id, title, description, price, visible) VALUES (4, 'Борщ', 'Борщ — овочева страва з м''ясом, традиційна страва на столі українців. Головним складником червоного борщу є столовий буряк.', 3500, 1);
INSERT INTO courses (id, title, description, price, visible) VALUES (5, 'Пончик', 'Пончик який обожнюють американські полісмени!', 5000, 1);
INSERT INTO courses (id, title, description, price, visible) VALUES (7, 'Плов', 'Плов  — страва східних кухонь з рису.', 8000, 1);
INSERT INTO courses (id, title, description, price, visible) VALUES (8, 'Макарони по-флотськи', 'Макарони по-флотськи дуже смачна та проста страва, приготування якої не займе багато часу!', 10000, 1);
INSERT INTO courses (id, title, description, price, visible) VALUES (9, 'Вареники', 'Вареники  — українська національна страва у вигляді відвареного тіста і начинки з картоплі з сметаною.', 5000, 1);
INSERT INTO courses (id, title, description, price, visible) VALUES (10, 'Стейк Денвер', 'Стейк - товстий шматок обсмаженого мяса.Стейк з кращих частин яловичини зазвичай називається просто стейком (іноді також біфштексом).', 20000, 1);
INSERT INTO courses (id, title, description, price, visible) VALUES (11, 'Фуа-гра', 'Фуа-гра - це паштет з гусячої печінки і символ гастрономічного шику — вважається винаходом французьких кулінарів, традиційна різдвяна страва у Франції.', 10000, 1);
INSERT INTO courses (id, title, description, price, visible) VALUES (12, 'Біф Веллінгтон', 'Біф Веллінгтон — це приготована вирізка яловичини, покрита паштетом (часто фуа-гра) та грибною пастою дюксель, яку потім загортають у листкове тісто та випікають.', 14000, 1);
INSERT INTO courses (id, title, description, price, visible) VALUES (13, 'Соте по-французьки', 'Жаб''ячі лапки являють собою делікатес, популярність якого зростає з кожним днем.', 20000, 1);
